import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

export default function Chart({currentCoin, filteredHistData}) {


const options = {
    chart: {
        zoomType: 'x'
    },
    title: {
        text: `${currentCoin} to USD exchange rate over time`
    },
    subtitle: {
        text: document.ontouchstart === undefined ?
            'Click and drag in the plot area to zoom in' : 'Pinch the chart to zoom in'
    },
    xAxis: {
        type: 'datetime'
    },
    yAxis: {
        title: {
            text: 'Price (USD)'
        }
    },
    legend: {
        enabled: false
    },
    plotOptions: {
        area: {
            fillColor: {
                linearGradient: {
                    x1: 0,
                    y1: 0,
                    x2: 0,
                    y2: 1
                },
                stops: [
                    [0, Highcharts.getOptions().colors[0]],
                    [1, Highcharts.color(Highcharts.getOptions().colors[0]).setOpacity(0).get('rgba')]
                ]
            },
            marker: {
                radius: 2
            },
            lineWidth: 1,
            states: {
                hover: {
                    lineWidth: 1
                }
            },
            threshold: null
        }
    },

    series: [{
        type: 'area',
        name: 'Price (USD)',
        data: filteredHistData
    }]
};

return (
    <HighchartsReact
        highcharts={Highcharts}
        options={options}
    />
)
}
