import './CoinData.css'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';

const CoinData = () => {

    const options = [
        'BTC/USD', 'two', 'three'
    ];
    const defaultOption = options[0];
    return (
        <div className="main-container">
            <div className="select-coin">
                <Dropdown className="dropdown" options={options} value={defaultOption} placeholder="Select an option"/>
                <Button className="subscribe-btn" variant="primary">Subscribe</Button>{' '}
            </div>
            <p style={{float:"left", marginLeft: "15px"}}>Market data:</p>
            <div className="market-data">
                <table border="1" width="100%" cellPadding="5">
                    <tr>
                        <th>Symbol:</th>
                        <th>Price:</th>
                        <th>Time:</th>
                    </tr>
                    <tr>
                        <td>BTC/USD</td>
                        <td>$48.384,433</td>
                        <td>Aug 7, 9:45 AM</td>
                    </tr>
                </table>

            </div>
        </div>
    )
}

export default CoinData;
