import './CoinData.css'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';
const Highcharts = require('highcharts');
require('highcharts/modules/exporting')(Highcharts);

const CoinData = () => {

    const options = [
        'BTC/USD', 'two', 'three'
    ];

    const defaultOption = options[0];


    const getData = async() => {
        await fetch("/",
            {
                method: "GET",
                headers: {
                    "Content-type": "application/json"
                },
                body: JSON.stringify({
                    name: "SomeName",
                    surname: "SomeSurname"
                })
            }
        ).then(resp => resp.json())
    }

    return (
        <div className="main-container">
            <div className="select-coin">
                <Dropdown className="dropdown" options={options} value={defaultOption} placeholder="Select an option"/>
                <Button className="subscribe-btn" variant="primary">Subscribe</Button>{' '}
            </div>
            <p style={{float: "left", marginLeft: "15px"}}>Market data:</p>
            <div className="market-data">
                <table width="100%" cellPadding="5">
                    <tr>
                        <th>Symbol:</th>
                        <th>Price:</th>
                        <th>Time:</th>
                    </tr>
                    <tr>
                        <td>BTC/USD</td>
                        <td>$48.384,433</td>
                        <td>Aug 7, 9:45 AM</td>
                    </tr>
                </table>
            </div>
            <div className="charting-data">
                <p style={{float: "left", marginLeft: "15px"}}>Charting data:</p>

            </div>
        </div>
    )
}

export default CoinData;
