import './CoinData.css'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';
import {useEffect, useState} from "react";
import axios from "axios";
import Highcharts from 'highcharts'
import HighchartsReact from 'highcharts-react-official'
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import Loader from "react-loader-spinner";

const CoinData = () => {
    const [data, setData] = useState([]);
    const [histPrice, setHistPrice] = useState([])
    const [currentCoin, setCurrentCoin] = useState()
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:3007/getAssetId')
            .then((response) => {
                setData(response.data)
            })
    }, [])

    let filteredHistData = histPrice.map((el) => [+new Date(el.time_open), Math.round(el.rate_open)]);

    const options = {
        chart: {
            zoomType: 'x'
        },
        title: {
            text: `${currentCoin} to USD exchange rate over time`
        },
        subtitle: {
            text: document.ontouchstart === undefined ?
                'Click and drag in the plot area to zoom in' : 'Pinch the chart to zoom in'
        },
        xAxis: {
            type: 'datetime'
        },
        yAxis: {
            title: {
                text: 'Price (USD)'
            }
        },
        legend: {
            enabled: false
        },
        plotOptions: {
            area: {
                fillColor: {
                    linearGradient: {
                        x1: 0,
                        y1: 0,
                        x2: 0,
                        y2: 1
                    },
                    stops: [
                        [0, Highcharts.getOptions().colors[0]],
                        [1, Highcharts.color(Highcharts.getOptions().colors[0]).setOpacity(0).get('rgba')]
                    ]
                },
                marker: {
                    radius: 2
                },
                lineWidth: 1,
                states: {
                    hover: {
                        lineWidth: 1
                    }
                },
                threshold: null
            }
        },

        series: [{
            type: 'area',
            name: 'Price (USD)',
            data: filteredHistData
        }]
    };

    const onSubscribeBtn = () => {
        axios.get(`http://localhost:3007/historicalPrice?coin=${currentCoin}`)
            .then((response) => {
                setHistPrice(response.data)
                setIsLoaded(true);
                console.log(histPrice.length)
            })
    }



    return (
        <div className="main-container">
            <div className="select-coin">
                <Dropdown className="dropdown" onChange={(e) => {
                    setCurrentCoin(e.value)
                }} options={data.map(el => el.asset_id)}
                          placeholder="Select an option"/>
                <Button className="subscribe-btn" onClick={onSubscribeBtn} variant="primary">Subscribe</Button>{' '}
            </div>
            <p>Market data</p>
            <div className="market-data">
                <table width="100%" cellPadding="5">
                    <tr>
                        <th>Symbol:</th>
                        <th>Price:</th>
                        <th>Time:</th>
                    </tr>
                    <tr>
                        <td>BTC/USD</td>
                        <td>$48.384,433</td>
                        <td>Aug 7, 9:45 AM</td>
                    </tr>
                </table>
            </div>
            <div className="charting-data">
                <p>Charting data</p>
                {isLoaded === false
                    ?
                    <Loader type="TailSpin" color="#0080ff" height={80} width={80}/>
                    :
                    <HighchartsReact
                        highcharts={Highcharts}
                        options={options}
                    />


                }



            </div>
        </div>
    )
}

export default CoinData;
