import './CoinData.css'
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button} from 'react-bootstrap';
import {useEffect, useState} from "react";
import axios from "axios";
const Highcharts = require('highcharts');
require('highcharts/modules/exporting')(Highcharts);

const CoinData = () => {
    const [data, setData] = useState([]);
    const [histPrice, setHistPrice] = useState([])

useEffect(() => {
    axios.get('http://localhost:3007/')
        .then((response) => {setData(response.data)})
    axios.get('http://localhost:3007/historicalPrice')
        .then((response) => {setHistPrice(response.data)})
}, [])

    Highcharts.getJSON(
        'https://cdn.jsdelivr.net/gh/highcharts/highcharts@v7.0.0/samples/data/usdeur.json',
        function (data) {

            Highcharts.chart('container', {
                chart: {
                    zoomType: 'x'
                },
                title: {
                    text: 'USD to EUR exchange rate over time'
                },
                subtitle: {
                    text: document.ontouchstart === undefined ?
                        'Click and drag in the plot area to zoom in' : 'Pinch the chart to zoom in'
                },
                xAxis: {
                    type: 'datetime'
                },
                yAxis: {
                    title: {
                        text: 'Exchange rate'
                    }
                },
                legend: {
                    enabled: false
                },
                plotOptions: {
                    area: {
                        fillColor: {
                            linearGradient: {
                                x1: 0,
                                y1: 0,
                                x2: 0,
                                y2: 1
                            },
                            stops: [
                                [0, Highcharts.getOptions().colors[0]],
                                [1, Highcharts.color(Highcharts.getOptions().colors[0]).setOpacity(0).get('rgba')]
                            ]
                        },
                        marker: {
                            radius: 2
                        },
                        lineWidth: 1,
                        states: {
                            hover: {
                                lineWidth: 1
                            }
                        },
                        threshold: null
                    }
                },

                series: [{
                    type: 'area',
                    name: 'USD to EUR',
                    data: data
                }]
            });
        }
    );

console.log("data here" + data)
console.log(histPrice)

    return (
        <div className="main-container">
            <div className="select-coin">
                <Dropdown className="dropdown" options={data.map(el=>el.asset_id)} placeholder="Select an option"/>
                <Button className="subscribe-btn"  variant="primary">Subscribe</Button>{' '}
            </div>
            <p style={{float: "left", marginLeft: "15px"}}>Market data:</p>
            <div className="market-data">
                <table width="100%" cellPadding="5">
                    <tr>
                        <th>Symbol:</th>
                        <th>Price:</th>
                        <th>Time:</th>
                    </tr>
                    <tr>
                        <td>BTC/USD</td>
                        <td>$48.384,433</td>
                        <td>Aug 7, 9:45 AM</td>
                    </tr>
                </table>
            </div>
            <div className="charting-data">
                <p style={{float: "left", marginLeft: "15px"}}>Charting data:</p>

            </div>
        </div>
    )
}

export default CoinData;
